sqlMap.insert:

	SELECT nextval('tbl_resource_resource_id_seq') as id;

insert into tbl_resource (resource_id, supplier_source_id, resource_type_id, org_unit_id, last_changed)
values (#resource.id#, #supplierSource.id#, #resource.resourceType.id#, #resource.offeredBy.organization.id#, now());

sqlMap.select:
select resource_id, supplier_source_id, resource_type_id, org_unit_id, last_changed
from tbl_resource
where resource_id = #id#;
