sqlMap.select:
select user_id, username, password, org_unit_id, person_id, last_changed
from tbl_user
where user_id = #userId#;

select user_id, username, password, org_unit_id, person_id, last_changed
from tbl_user
where username = #userName#;

sqlMap.insert:

	SELECT nextval('tbl_user_user_id_seq') as id;

insert into tbl_user
(user_id, username, password, org_unit_id, person_id, last_changed) 
values 
(#id#, #username#, #password#, #organization.id#, #person.id#, now());
