sqlMap.select:
select org_unit_id, org_unit_parent_id, descr, org_type_id, contact_information_id, person_id, last_changed
from tbl_org_unit
where org_unit_id = #organizationId#;

sqlMap.insert:

	SELECT nextval('tbl_org_unit_org_unit_id_seq') as id;

insert into tbl_org_unit (org_unit_id, org_unit_parent_id, descr, org_type_id, contact_information_id, person_id, last_changed) 
values (#id#, #parent.id#, #description#, #type.id#, #contactInformation.id#, #contactPerson.id#, now());
