sqlMap.insert:

	SELECT nextval('tbl_person_person_id_seq') as id;

insert into tbl_person
(person_id, first_name, last_name, student_number, hpr_number, contact_information_id, profile_id, position_type_id, employer, position_text, is_student, last_changed)
values 
(#id#, #firstName#, #lastName#, #studentNumber#, #hprNumber#, #contactInformation.id#, #profile.id#, #position.id#, #employer#, #positionText#, #isStudent#, now());
