sqlMap.insert:

	SELECT nextval('tbl_resource_resource_id_seq') as resourceId;

insert into tbl_resource (resource_id, supplier_source_id, resource_type_id, org_unit_id, last_changed)
values (#resourceId#, #supplierSource.id#, #resourceType.id#, #offeredBy.id#, now());

sqlMap.select:
select resource_id, supplier_source_id, resource_type_id, org_unit_id, last_changed
from tbl_resource
where resource_id = #resourceId#;
