sqlMap.insert:

	SELECT nextval('tbl_supplier_source_supplier_source_id_seq') as supplierSourceId;

insert into tbl_supplier_source (supplier_source_name, url, supplier_source_id, last_changed)
values (#supplierSourceName#, #url.stringValue#, #supplierSourceId#, now());

sqlMap.select:
select supplier_source_name, url, supplier_source_id, last_changed
from tbl_supplier_source
where supplier_source_id = #supplierSourceId#;
